window.onload=function(){document.querySelectorAll(".peak-faq").forEach(e=>{const t=e.querySelector(".peak-faq-answer");t.style.display="block";let a=t.offsetHeight+"px";t.style.height="0px",e.querySelector(".peak-faq-question").addEventListener("click",function(o){o.preventDefault();let s=e.classList.contains("peak-faq-open");s?e.classList.remove("peak-faq-open"):e.classList.add("peak-faq-open"),t.style.height=s?"0px":a})})};